    expert_name=#{expert_name},


    phonenumber=#{phonenumber},

height=#{height},weight=#{weight},worktime=#{worktime},selfintro=#{selfintro},
profile_picture=#{profile_picture}
where expert_id=#{expert_id}

update expertinfo set

    password=#{password}

where expert_id=#{expert_id}
